function validateBlock(block: Block, parentBlock: Block) {
    assert(block.parentHash === hash(rlp(parentBlock)))
    assert(block.number === parentBlock.number + 1n)
    assert(block.gasTarget >= parentBlock.gasTarget - parentBlock.gasTarget / 1024n)
    assert(block.gasTarget <= parentBlock.gasTarget + parentBlock.gasTarget / 1024n)
    assert(block.timestamp > parentBlock.timestamp)
    assert(block.baseFee === parentBlock.baseFee + parentBlock.baseFee * (parentBlock.gasUsed - parentBlock.gasTarget) / parentBlock.gasUsed / 8n)
    const blocksSinceStartOfMigration = block.number - EIP1559_FORK_BLOCK_NUMBER
    const eip1559GasTarget = (block.number < EIP1559_FORK_BLOCK_NUMBER) ? 0n
        : (block.number > EIP1559_FORK_BLOCK_NUMBER + EIP1559_MIGRATION_DURATION) ? block.gasTarget
        : (block.gasTarget / 2n) + (block.gasTarget / 2n) * (blocksSinceStartOfMigration / EIP1559_MIGRATION_DURATION)
    const legacyGasTarget = block.gasTarget - eip1559GasTarget
    const legacyTransactions = block.transactions.filter(tx => tx.type !== EIP1559_TRANSACTION_TYPE)
    const eip1559Transactions = block.transactions.filter(tx => tx.type === EIP1559_TRANSACTION_TYPE)
    assert(legacyTransactions.reduce((total, tx) => total += tx.gasUsed, 0n) <= legacyGasTarget)
    assert(eip1559Transactions.reduce((total, tx) => total += tx.gasUsed, 0n) <= eip1559GasTarget)
    // TODO: validate uncles
    // TODO: validate stateRoot
    // TODO: validate transactionsRoot
    // TODO: validate receiptsRoot
    // TODO: validate logsBloom
    // TODO: validate difficulty
    // TODO: validate nonce
}
